/**
 * @typedef {{
 *   start: PointerEvent;
 *   x: number;
 *   y: number;
 *   dx: number;
 *   dy: number;
 *   context: Record<string, any>
 * }} Drag
 */
/**
 * @param {HTMLElement} node
 * @param {{
 *   start?: (drag: Drag, e: PointerEvent) => void;
 *   move?: (drag: Drag, e: PointerEvent) => void;
 *   end?: (drag: Drag, e: PointerEvent) => void;
 * }} handlers
 */
export function drag(node: HTMLElement, { start, move, end }?: {
    start?: (drag: Drag, e: PointerEvent) => void;
    move?: (drag: Drag, e: PointerEvent) => void;
    end?: (drag: Drag, e: PointerEvent) => void;
}): void;
export type Drag = {
    start: PointerEvent;
    x: number;
    y: number;
    dx: number;
    dy: number;
    context: Record<string, any>;
};
